components/amp/css/base:
*,
*:after,
*:before {
    box-sizing: border-box;
}
html {
    font-size: 14px
}
html,
body {
    height: auto
}

body {
    font-size: 1rem;
    font-family: "Karla", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: 1.5;
    color: #4f4f4f;
    padding-top: 55px;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
ul {
    margin: 0;
    padding: 0;
}
li {
    padding: 0;
    margin: 0;
}
a {
    color: #4f4f4f;
    line-height: inherit;
    text-decoration: none;
}
a:hover,
a:focus {
    color: #a5a5a5
}
p {
    margin-bottom: 1.5rem
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #2d2d2d;
    font-family: "Montserrat", Arial, Helvetica, sans-serif;
    font-weight: 400;
    letter-spacing: .25px;
    margin-bottom: .78571rem;
    margin-top: 2.28571rem
}
h1 small,
h2 small,
h3 small,
h4 small,
h5 small,
h6 small {
    color: #989898;
    font-size: 60%;
    line-height: 0
}
h1 {
    font-size: 28px
}
h2 {
    font-size: 25px
}
h3 {
    font-size: 22px
}
h4 {
    font-size: 20px
}
h5 {
    font-size: 15px
}
h6 {
    font-size: 13px;
    text-transform: uppercase
}

hr {
    border: solid #e8e8e8;
    border-width: 1px 0 0;
    clear: both;
    height: 0;
    margin: 2rem 0 1.92857rem
}
em,
i {
    font-style: italic;
    line-height: inherit
}
strong,
b {
    font-weight: 700;
    line-height: inherit
}
small {
    font-size: 60%;
    line-height: inherit
}
ul,
ol,
dl {
    list-style-position: outside;
    margin-bottom: 1.5rem
}
ul ul,
ul ol,
ol ul,
ol ol {
    margin-left: 1.2rem;
    margin-bottom: 0
}
ul {
    margin-left: 1.15rem
}
ol {
    margin-left: 1.15rem
}
dl dt {
    font-weight: 700;
}
abbr,
acronym {
    border-bottom: 1px dotted #dfdfdf;
    color: #4f4f4f;
    cursor: help;
    font-size: 90%;
    text-transform: uppercase
}
abbr {
    text-transform: none
}
blockquote,
blockquote p {
    font-size: 22px;
    line-height: 29px
}
cite {
    color: #a5a5a5;
    display: block;
    font-size: 1rem;
    line-height: 1.5;
}
.icon {
    height: 1.14286rem;
    width: 1.14286rem;
    display: inline-block;
    vertical-align: middle
}
.icon svg {
    display: inline-block;
    fill: #4f4f4f;
    height: 100%;
    vertical-align: top;
    width: 100%
}
.is-srOnly,
.modal {
    display: none;
}

.body {
    margin-bottom: 6rem;
    margin-top: 1.5rem
}

.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 0 1.5rem;
    position: relative;
}

.button {
    border-radius: 0;
    border-style: solid;
    border-width: 1px;
    cursor: pointer;
    font-family: "Montserrat", Arial, Helvetica, sans-serif;
    font-weight: 400;
    line-height: normal;
    margin: 0 0 1rem;
    position: relative;
    text-align: center;
    text-decoration: none;
    display: inline-block;
    background-color: transparent;
    border-color: #dfdfdf;
    color: #454545;
    font-size: 1rem;
    padding: .85714rem 2.28571rem;
    border-radius: 4px;
    outline: none;
    vertical-align: middle
}
.button:hover,
.button:focus,
.button.is-active,
.button.active {
    background-color: transparent;
    border-color: #989898;
    color: #666
}
.button:active {
    background-color: transparent;
    border: 1px solid #454545;
    color: #454545
}
.button:focus,
.button.is-active,
.button.active {
    outline: none
}
.button--primary {
    background-color: #454545;
    border-color: #454545;
    color: #fff
}
.button--primary:hover,
.button--primary:focus,
.button--primary.is-active,
.button--primary.active {
    background-color: #666;
    border-color: #666;
    color: #fff
}
.button--primary:active {
    background-color: #989898;
    border: 1px solid #989898;
    color: #fff
}
.button[disabled] {
    background-color: #dadada;
    border-color: #dadada;
    color: #fff;
    cursor: default
}

.button--wide {
    display: block;
    width: 100%
}

.button--icon {
    font-size: 1rem;
    padding: .78571rem
}
.button--icon svg {
    fill: #4f4f4f
}

.icon--ratingEmpty svg {
    fill: #dfdfdf;
}

.icon--ratingFull svg {
    fill: #454545;
}

.price--rrp,
.price--discounted {
    text-decoration: line-through;
}

components/amp/css/category:
.container {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: .5rem 1.5rem;
}

.categoryView-img {
    padding: 0 0 1rem;
}

.categoryView-title {
    font-size: 20px;
    margin: 7px 0;
    text-align: center;
}

.sidebarBlock {
    margin-left: auto;
    margin-right: auto;
    max-width: 91.71429rem;
    padding: 1rem 1.5rem;
    background-color: #f3f4f5;
    text-align: center;
    border-top: 1px solid #e8e8e8;
}

.sidebarBlock-heading {
    margin: .78571rem 0;
}

.navList {
    list-style: none;
    padding: 0;
    margin-left: 0;
}

.navList-action {
    color: #4f4f4f;
    font-size: 15px;
    padding: 3px 0;
    display: block;
}

.actionBar-section .form-field {
    background-color: white;
    border: 1px solid #dfdfdf;
    border-radius: 4px;
    overflow: hidden;
    display: inline-block;
}

.actionBar-section .form-label {
    color: #989898;
    font-size: 13px;
    line-height: 2.14286rem;
    margin-bottom: 0;
    padding-left: 0.78571rem;
    display: inline-block;
}

.actionBar-section .form-select {
    color: #4f4f4f;
    height: 2.14286rem;
    display: inline-block;
}

.productGrid {
    display: flex;
    flex-wrap: wrap;
    list-style: none;
    margin: 0;
    padding: 0;
}

.productGrid .product {
    width: 50%;
    padding: 1rem 1.5rem;
}

.productList {
    list-style: none;
    margin: 0;
    padding: 0;
}

.productList .product {
    padding: 1rem 1.5rem;
}

.listItem-rating,
.listItem-brand,
.listItem-title {
    margin: .5rem 0;
}

.listItem-title {
    font-size: 15px;
}

.listItem-price {
    margin-bottom: 1rem;
}


.card-figure {
    margin: 0;
    padding: 0;
    position: relative;
}

.card-image {
    max-width: 100%;
    height: auto;
    margin-bottom: 10px;
}

.card-body {
    text-align: center;
}

.card-text,
.card-title {
    margin: 5px 0;
}

.card-text {
    color: #989898;
    font-size: 14px;
    font-weight: normal;
}

.card-title {
    font-size: 15px;
}

.rating--small {
    display: block;
    margin-bottom: 10px;
}

.sale-sash {
    background: #007dc6;
    color: #ffffff;
    font-size: 15px;
    padding: 1px 5px;
    position: absolute;
    text-align: center;
    bottom: 0;
    width: 100%;
    z-index: 10;
}

.pagination {
    margin: 2rem 1.5rem 2.28571rem;
}

.pagination-list {
    display: block;
    margin: 0;
    text-align: center;
    list-style: none;
    padding: 0 80px;
}

.pagination-item {
    display: inline-block;
    border: 1px solid transparent;
    border-radius: 4px;
    font-size: 15px;
    position: relative;
}

.pagination-link {
    display: block;
    padding: 3px 10px;
}

.pagination-item--current {
    border-color: #e8e8e8;
}

.pagination-item--previous {
    position: absolute;
    left: 1rem;
}

.pagination-item--next {
    position: absolute;
    right: 1rem;
}

components/amp/css/footer:
.amp-footer {
    color: #989898;
    text-align: center;
    border-top: 1px solid #e8e8e8
}

.amp-footer a {
    color: #989898;
    text-decoration: none
}
.amp-footer a:hover {
    color: #4f4f4f
}

.amp-footer-heading {
    text-transform: capitalize;
}

.amp-footer-list {
    margin: 0;
    list-style-type: none;
}

.amp-footer-list li {
    padding: 3px 0;
}

components/amp/css/header:
.amp-menu-button {
    background-color: transparent;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="-3 -6 24 24"><path fill="#4f4f4f" d="M0 12h18v-2H0v2M0 7h18V5H0v2M0 0v2h18V0H0"/></svg>');
    background-repeat: no-repeat;
    background-size: 2rem 1.8rem;
    background-position: center center;
    width: 3.92857rem;
    height: 3.92857rem;
    cursor: pointer;
    align-self: center;
    font-size: 0;
    border: 0;
    flex-shrink: 0;
}

.amp-header {
    background-color: #fff;
    border-bottom: 1px solid #e8e8e8;
    position: fixed;
    top: 0;
    width: 100%;
    height: 55px;
    z-index: 20;
    display: flex;
    align-items: center;
}

.amp-header-link {
    display: inline-block;
    text-decoration: none;
}

.amp-header-logo {
    flex-grow: 1;
    padding-right: 3.92857rem;
    text-align: center;
}

.amp-empty-space {
    width: 3.92857rem;
}

.amp-header-logo-text {
    display: block;
    font-family: "Oswald", Arial, Helvetica, sans-serif;
    font-size: 20px;
    font-weight: 300;
    letter-spacing: .35714rem;
    margin-left: auto;
    margin-right: auto;
    overflow: hidden;
    padding: .21429rem 0;
    text-overflow: ellipsis;
    text-transform: uppercase;
    white-space: nowrap
}

.banners {
    background-color: #4f4f4f;
    color: #fff;
    padding: .78571rem 0;
    text-align: center
}
.banners p:last-child {
    margin-bottom: 0
}
.banners a {
    color: #fff
}
.banner {
    margin: 0 auto;
    max-width: 85.71429rem;
    width: 100%
}
.banner:before,
.banner:after {
    content: " ";
    display: table
}
.banner:after {
    clear: both
}
.banner + .banner {
    margin-top: 1.5rem
}


.form-fieldset {
    border-width: 0;
    margin: 0;
    padding: 0
}


.form-input {
    appearance: none;
    background-color: #fff;
    border-color: #dfdfdf;
    border-style: solid;
    border-width: 1px;
    border-radius: 4px;
    color: #454545;
    display: block;
    font-family: inherit;
    font-size: 1rem;
    font-smoothing: antialiased;
    height: 3rem;
    margin: 0;
    padding: .75rem 1rem;
    transition: border-color 100ms ease-out;
}
.form-input:disabled {
    background-color: #fff;
    color: #a5a5a5;
    cursor: not-allowed
}
.form-input[readonly] {
    background-color: #f8f8f8;
    border-color: #454545;
    color: #989898;
    cursor: text
}
.form-input:focus {
    border-color: #989898;
    outline: none
}

.navPages {
    background-color: #f2f2f2;
    height: 100%;
    overflow-y: auto;
    padding: 1.5rem
}

.navPages-list {
    margin: .78571rem 0 0;
    padding: 0;
    list-style: none;
}
.navPages-list + .navPages-list {
    border-top: 1px solid #dfdfdf;
    padding-top: .78571rem
}
.navPages-action {
    display: block;
    font-size: 15px;
    font-weight: 700;
    padding: .78571rem 0;
    text-decoration: none;
    text-transform: uppercase
}
